	static boolean isDateValid(String dateStr) {
		DateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
		sdf.setLenient(false);
		try {
			sdf.parse(dateStr);
		} catch(ParseException e) {
			return false;
		}
		return true;
	}

